'use strict';

var varint = require('varint');

var isBuffer = require('is-buffer');

module.exports = function (buf) {
  if (!isBuffer(buf)) {
    throw new Error('arg needs to be a buffer');
  }

  var result = [];

  while (buf.length > 0) {
    var num = varint.decode(buf);
    result.push(num);
    buf = buf.slice(varint.decode.bytes);
  }

  return result;
};
