import { useEffect, useState } from 'react'
import { wallet } from 'tulip-data'

const WALLET_INFO_DEFAULT = {
  addressExist: false,
  addressUnique: false,
}

export function useWalletData(account) {
  const [walletInfo, setWalletInfo] = useState(WALLET_INFO_DEFAULT)

  useEffect(() => {
    let cancelled = false
    if (!account) {
      return setWalletInfo(info => ({ ...info, fetching: false }))
    }

    const fetchWalletData = async () => {
      try {
        const balances = await wallet.tokenBalances({
          user_address: account,
        })

        console.log(balances)
        if (!cancelled) {
          return setWalletInfo(balances)
        }
      } catch (err) {
        console.error(`Could not fetch wallet data `, err)
      }
    }

    fetchWalletData()
    return () => {
      cancelled = true
    }
  }, [account])

  return walletInfo
}
