'use strict';

var mergeOptions = require('merge-options');

var ipldDagCbor = require('ipld-dag-cbor');

var ipldDagPb = require('ipld-dag-pb');

var ipldRaw = require('ipld-raw');

module.exports = function (blockService, options) {
  options = options || {};
  return mergeOptions.call( // ensure we have the defaults formats even if the user overrides `formats: []`
  {
    concatArrays: true
  }, {
    blockService: blockService,
    formats: [ipldDagCbor, ipldDagPb, ipldRaw]
  }, options);
};
