import { useEffect, useMemo, useState } from 'react'
import { wallet } from 'tulip-data'
import { useWallet } from 'use-wallet'

export function useWalletData() {
  const [walletInfo, setWalletInfo] = useState([])
  const { account } = useWallet()

  useEffect(() => {
    let cancelled = false
    if (!account) {
      return setWalletInfo([])
    }

    const fetchWalletData = async () => {
      try {
        const balances = await wallet.tokenBalances({
          user_address: account,
        })
        if (!cancelled) {
          return setWalletInfo(balances)
        }
      } catch (err) {
        console.error(`Could not fetch wallet data `, err)
      }
    }

    fetchWalletData()
    return () => {
      cancelled = true
    }
  }, [account])

  return walletInfo
}

export function useNetBalance() {
  const walletInfo = useWalletData()
  return useMemo(() => {
    if (!walletInfo) {
      return 0
    }

    let netBalance = 0
    netBalance += walletInfo.map(value => {
      console.log(value)
      if (value && value.valueUSD) {
        return value.valueUSD
      }
    })

    return netBalance
  }, [walletInfo])
}
