import React from 'react'
import PropTypes from 'prop-types'
import { EthIdenticon, GU, textStyle, useTheme } from '@1hive/1hive-ui'

import HeaderModule from '../Header/HeaderModule'
import useProfileName from '../../hooks/useProfileName'
import { useWallet } from 'use-wallet'
import { shortenAddress } from '../../lib/web3-utils'

function AccountButton({ label, onClick }) {
  const theme = useTheme()
  const wallet = useWallet()
  const profileName = useProfileName(wallet.account)
  const RADIUS = 1.75 * GU

  return (
    <HeaderModule
      icon={
        <div css="position: relative">
          <EthIdenticon address={wallet.account} radius={RADIUS} />
          <div
            css={`
              position: absolute;
              bottom: -3px;
              right: -3px;
              width: 10px;
              height: 10px;
              background: ${theme.positive};
              border: 2px solid ${theme.surface};
              border-radius: 50%;
            `}
          />
        </div>
      }
      content={
        <>
          <div
            css={`
              margin-bottom: -5px;
              ${textStyle('body2')}
            `}
          >
            <div
              css={`
                overflow: hidden;
                max-width: ${16 * GU}px;
                text-overflow: ellipsis;
                white-space: nowrap;
                color: ${theme.content};
              `}
            >
              {profileName || shortenAddress(wallet.account)}
            </div>
          </div>
          <div
            css={`
              font-size: 11px; /* doesnâ€™t exist in aragonUI */
              color: ${theme.content};
            `}
          >
            Connected to {wallet.networkName}
          </div>
        </>
      }
      onClick={onClick}
    />
  )
}
AccountButton.propTypes = {
  label: PropTypes.string,
  onClick: PropTypes.func.isRequired,
}

export default AccountButton
