import React, { Component } from 'react'
import { Link } from '@1hive/1hive-ui'
import moment from 'moment'
import data from './ExampleData.json'
import LineChart from './LineChart'
import ToolTip from './ToolTip'
import './HomeChart.css'

class HomeChart extends Component {
  constructor(props) {
    super(props)
    this.state = {
      hoverLoc: null,
      activePoint: null,
      selectedRange: '1W',
    }
  }

  handleChartHover(hoverLoc, activePoint) {
    this.setState({
      hoverLoc: hoverLoc,
      activePoint: activePoint,
    })
  }

  componentDidMount() {
    const loadExampleData = () => {
      if (this.data !== undefined && this.data.count > 0) {
        return
      }

      const sortedData = []
      let count = 0
      for (const date in data.bpi) {
        sortedData.push({
          d: moment(date).format('MMM DD'),
          p: data.bpi[date].toLocaleString('us-EN', {
            style: 'currency',
            currency: 'USD',
          }),
          x: count,
          y: data.bpi[date],
        })
        count++
      }

      this.setState({
        data: sortedData,
      })
    }

    loadExampleData()
  }

  handleSelectDateRange(range) {
    this.setState(
      {
        selectedRange: range,
      },
      this.props.handleSelectDateRange(range)
    )
  }

  render() {
    return (
      <div className="container">
        <div className="row">
          <div
            css={`
              display: flex;
              justify-content: space-between;
              width: ${12.5 * 8}px;
            `}
          >
            <Link
              external={false}
              css={`
                color: ${this.state.selectedRange === '1W'
                  ? '#2C3437'
                  : '#818181'};
              `}
            >
              <span
                onClick={() => {
                  this.handleSelectDateRange('1W')
                }}
                css={`
                  ${this.state.selectedRange === '1W'
                    ? ''
                    : 'font-weight: 300;'}
                `}
              >
                1W
              </span>
            </Link>
            <Link
              external={false}
              css={`
                ${this.state.selectedRange === '1M'
                  ? 'color: #2C3437;'
                  : 'color: #818181'}
              `}
            >
              <span
                onClick={() => {
                  this.handleSelectDateRange('1M')
                }}
              >
                1M
              </span>
            </Link>
            <Link
              external={false}
              css={`
                color: ${this.state.selectedRange === '1W'
                  ? '#2C3437'
                  : '#818181'};
              `}
            >
              <span
                onClick={() => {
                  this.handleSelectDateRange('1W')
                }}
                css={`
                  ${this.state.selectedRange === '1Y'
                    ? ''
                    : 'font-weight: 300;'}
                `}
              >
                1Y
              </span>
            </Link>
          </div>
        </div>
        <div className="row">
          <div className="popup">
            {this.state.hoverLoc ? (
              <ToolTip
                width={this.props.width}
                activePoint={this.state.activePoint}
              />
            ) : null}
          </div>
        </div>
        <div className="row">
          <div className="chart">
            <LineChart
              data={this.state.data}
              onChartHover={(a, b) => this.handleChartHover(a, b)}
              height={this.props.height}
              width={this.props.width}
            />
          </div>
        </div>
      </div>
    )
  }
}

HomeChart.defaultProps = {
  data: [],
  color: '#7CE0D6',
  height: 300,
  width: 900,
}

export default HomeChart
