import { useEffect, useState } from 'react'
import { wallet } from 'tulip-data'
import { useWallet } from 'use-wallet'

const WALLET_INFO_DEFAULT = {
  addressExist: false,
  addressUnique: false,
}

export function useWalletData() {
  const [walletInfo, setWalletInfo] = useState(WALLET_INFO_DEFAULT)
  const { account } = useWallet()
  console.log('>>>>', account)

  useEffect(() => {
    let cancelled = false
    // if (!account) {
    //   return setWalletInfo(info => ({ ...info, fetching: false }))
    // }

    const fetchWalletData = async () => {
      try {
        const balances = await wallet.tokenBalances({
          user_address: '0x986a2fCa9eDa0e06fBf7839B89BfC006eE2a23Dd',
        })

        console.log('WW', wallet)
        if (!cancelled) {
          return setWalletInfo(balances)
        }
      } catch (err) {
        console.error(`Could not fetch wallet data `, err)
      }
    }

    fetchWalletData()
    return () => {
      cancelled = true
    }
  }, [account])

  return walletInfo
}
