import React from 'react'
import { DataView, textStyle } from '@1hive/1hive-ui'
import PairName from '../PairName'
import Fuse from 'fuse.js'

const AssetsTable = props => {
  const assets = props.assets || []
  console.log(assets)
  const fuse = new Fuse(assets, {
    keys: ['name', 'symbol'],
  })
  const results = fuse.search(props.searchValue)

  return (
    <div
      css={`
        ${textStyle('body2')};
        font-family: 'Overpass', sans-serif;
        font-weight: 300;
        font-size: 18px;
      `}
    >
      <DataView
        fields={['Asset', 'Balance', 'Price', 'Value']}
        css={`
          border-top: none;
        `}
        emptyState={{
          default: {
            displayLoader: false,
            title: 'Getting Assets',
            subtitle: null,
            illustration: null,
            clearLabel: null,
          },
          loading: {
            displayLoader: true,
            title: 'No data available.',
            subtitle: null,
            illustration: null,
            clearLabel: null,
          },
        }}
        entries={props.searchValue ? results : assets}
        header
        renderEntry={({
          name,
          symbol,
          balance,
          price,
          value,
          image1,
          image2,
        }) => {
          const customLabel = name
          const imgObj = {
            pair1: image1,
            pair2: image2,
          }
          return [
            <PairName
              image={imgObj}
              name={customLabel}
              subheadline="Honeyswap"
            />,
            <p>{balance}</p>,
            <p>$ {price}</p>,
            <p>$ {value}</p>,
          ]
        }}
      />
    </div>
  )
}
export default AssetsTable
