import React, { useContext } from 'react'
import PropTypes from 'prop-types'
import { useHoneyCombConfigSubscription } from '../hooks/subscription-hooks'
import { getNetworkConfig } from '../networks'

const HoneyCombConfigContext = React.createContext()

function HoneyCombConfigProvider({ children }) {
  const courtAddress = getNetworkConfig().court
  const courtConfig = useHoneyCombConfigSubscription(courtAddress)

  return (
    <HoneyCombConfigContext.Provider value={courtConfig}>
      {children}
    </HoneyCombConfigContext.Provider>
  )
}

HoneyCombConfigProvider.propTypes = {
  children: PropTypes.node,
}

function useHoneyCombConfig() {
  return useContext(HoneyCombConfigContext)
}

export { HoneyCombConfigProvider, useHoneyCombConfig }
