import React, { useEffect, useState } from 'react'
import { GU, textStyle, useLayout } from '@1hive/1hive-ui'
import DropdownComponent from '../Farming/Dropdown'
import sushiData from '@sushiswap/sushi-data'
import FarmTable from '../Farming/FarmTable'
import SearchComponent from '../Farming/Search'

const AssetList = React.memo(() => {
  const [farmData, setFarmData] = useState([])
  const [pairs, setPairs] = useState([])
  const [search, setSearch] = useState('')
  const { layoutName } = useLayout()
  const compactMode = layoutName === 'small' || layoutName === 'medium'

  const dropdownItems = {
    networkItems: ['xDai'],
    platformItems: ['Platform1'],
  }
  useEffect(() => {
    sushiData.masterchef.apys().then(data => {
      setFarmData(data)
      const pairArray = data.map(pair => {
        return pair.pair
      })
      sushiData.exchange.pairs(pairArray).then(res => {
        const result = res.filter(r => {
          return r.reserveETH > 1 && r.totalSupply > 1
        })
        setPairs(result)
      })
    })
  }, [])
  const handleSearch = value => {
    setSearch(value)
  }
  return (
    <div
      css={`
        padding-top: ${3 * GU}px;
        font-family: 'Overpass', sans-serif;
      `}
    >
      <h1
        css={`
          font-weight: 300;
          ${textStyle('title3')};
        `}
      >
        Assets
      </h1>
      <div
        css={`
          display: flex;
          width: 100%;
          justify-content: space-between;
          flex-direction: row;
        `}
      >
        <div
          css={`
            width: 35%;
            display: flex;
            marting-top: ${1.5 * GU}px;
            flex-direction: ${!compactMode ? 'row' : 'column'};
            justify-content: space-between;
          `}
        >
          <DropdownComponent items={dropdownItems.networkItems} />
          <DropdownComponent
            items={dropdownItems.platformItems}
            css={`
              margin-top: ${compactMode ? GU : 0}px;
            `}
          />
        </div>
        <SearchComponent
          passSearch={handleSearch}
          placeholder="Search by asset"
        />
      </div>
      <div
        css={`
          padding-top: ${3 * GU}px;
          ${textStyle('title1')};
        `}
      >
        <FarmTable tableData={farmData} pairData={pairs} searchValue={search} />
      </div>
    </div>
  )
})

export default AssetList
